import { HttpClient, HttpHeaders } from "@angular/common/http";
import { Component, OnInit } from "@angular/core";
import { FormBuilder, FormGroup, Validators } from "@angular/forms";
import { Title } from "@angular/platform-browser";
import { ActivatedRoute, Router } from "@angular/router";
import { NgbModal, NgbModalOptions } from "@ng-bootstrap/ng-bootstrap";
import { Ng4LoadingSpinnerService } from "ng4-loading-spinner";
import { SessionService } from "src/app/session.service";
import { environment } from "src/environments/environment";

@Component({
  selector: "app-dapaymenthistory",
  templateUrl: "./dapaymenthistory.component.html",
  styleUrls: ["./dapaymenthistory.component.scss"],
})
export class DapaymenthistoryComponent implements OnInit {
  apiUrl: string;
  paymentHistoryData: any;
  dtOptions: any = {};
  modalOptions: NgbModalOptions;
  searchForm: FormGroup;
  submitted: boolean;
  apiResponseMessage: any;
  roleID: string;
  title = "Direct Assessmennt - Payment History Report";
  showTaxOffice: boolean = false;
  taxTaxOffices: any;
  taxOffice: any;

  config: any;
  currentPageLength: any = 10;
  searchObject: any = {};

  constructor(
    private formBuilder: FormBuilder,
    private httpClient: HttpClient,
    private router: Router,
    private route: ActivatedRoute,
    private sess: SessionService,
    private titleService: Title,
    private modalService: NgbModal,
    private spinnerService: Ng4LoadingSpinnerService
  ) {}

  ngOnInit(searchObj = null): void {
    this.sess.checkLogin();
    this.roleID = localStorage.getItem("admin_role_id");

    if (this.roleID === "1") {
      this.showTaxOffice = true;
    }

    this.titleService.setTitle(this.title);
    this.getTaxOffices();
    this.intialiseTableProperties();

    if (searchObj == null || searchObj == "") {
      this.paymentHistoryData = "";
      this.initialiseForm();
      this.intialiseTableProperties();
      //this.getPaymentHistory();

      /* Pagination Start */
      this.config = {
        currentPage: 1,
        itemsPerPage: 10,
      };

      this.getPaymentHistory(this.config.itemsPerPage, this.config.currentPage);

      this.route.queryParams.subscribe(
        (params) =>
          (this.config.currentPage = params["page"] ? params["page"] : 1)
      );

      // console.log("searchObj is null");
      this.initialiseForm();
    } else {
      this.paymentHistoryData = "";
      //this.getPaymentHistory(searchObj);

      /* Pagination Start */
      this.config = {
        currentPage: 1,
        itemsPerPage: 10,
      };

      this.getPaymentHistory(this.config.itemsPerPage, this.config.currentPage);

      this.route.queryParams.subscribe(
        (params) =>
          (this.config.currentPage = params["page"] ? params["page"] : 1)
      );

      // console.log("searchObj is not null");
    }
  }

  public daterange: any = {};

  // see original project for full list of options
  // can also be setup using the config service to apply to multiple pickers
  public options: any = {
    locale: { format: "YYYY-MM-DD" },
    alwaysShowCalendars: false,
  };

  public selectedDate(value: any, datepicker?: any) {
    // this is the date  selected
    console.log("selectedDate: ", value);

    // any object can be passed to the selected event and it will be passed back here
    // datepicker.start = value.start;
    // datepicker.end = value.end;

    // use passed valuable to update state
    this.daterange.start = value.start.format("YYYY-MM-DD");
    this.daterange.end = value.end.format("YYYY-MM-DD");
    this.daterange.label = value.label;
  }

  initialiseForm() {
    this.searchForm = this.formBuilder.group({
      paymentChannel: [""],
      taxOfficeID: [""],
      // taxYear: [''],
      taxYear: [
        "",
        [
          Validators.pattern(/^(19|20)\d{2}$/),
          Validators.minLength(4),
          Validators.maxLength(4),
        ],
      ],
      name: [""],
      // dateFrom: [''],
      // dateTo: [''],
      daterangeInput: [""],
    });
  }

  intialiseTableProperties() {
    this.modalOptions = {
      backdrop: "static",
      backdropClass: "customBackdrop",
      // size: 'lg'
      size: "xl",
    };

    this.dtOptions = {
      paging: false,
      pagingType: "full_numbers",
      responsive: true,
      pageLength: 10,
      lengthChange: false,
      processing: false,
      ordering: false,
      info: false,
      columnDefs: [
        {
          //targets: [ 10 ],
          visible: false,
          searchable: false,
        },
      ],
      dom:
        "<'row'<'col-sm-4'l><'col-sm-4 text-center'B><'col-sm-4'f>>" +
        "<'row'<'col-sm-12'tr>>" +
        "<'row'<'col-sm-5'i><'col-sm-7'p>>",
      buttons: [
        {
          extend: "csv",
          className: "btn btn-outline-dark export-btn",
          text: '<i class="fas fa-file-csv"> CSV</i>',
          exportOptions: {
            columns: [0, 1, 2, 3, 4, 5, 6, 7],
          },
        },
        {
          extend: "excel",
          className: "btn btn-outline-dark export-btn",
          text: '<i class="fas fa-file-excel"> Excel</i>',
          exportOptions: {
            columns: [0, 1, 2, 3, 4, 5, 6, 7],
          },
        },

        {
          extend: 'pdfHtml5',
          className: "btn btn-outline-dark export-btn",
          text: '<i class="fas fa-file-pdf"> PDF</i>',
          orientation: "portrait",
          pageSize: "LEGAL",
          exportOptions: {
            columns: [0, 1, 2, 3, 4, 5, 6, 7],
          },

          customize: function (doc) {
              doc.content.splice( 1, 0, {
                  margin: [ 0, 0, 0, 10 ],
                  alignment: 'left',
                  image: 'data:image/png;base64,',
              },
              
            );

            // doc.pageMargins = [ 40, 60, 40, 60 ];
          }
        }
      ],
    };

    if (this.roleID === "1") {
      this.dtOptions.buttons = [
        {
          extend: "csv",
          className: "btn btn-outline-dark export-btn",
          text: '<i class="fas fa-file-csv"> CSV</i>',
          exportOptions: {
            columns: [0, 1, 2, 3, 4, 5, 6, 7, 8],
          },
        },
        {
          extend: "excel",
          className: "btn btn-outline-dark export-btn",
          text: '<i class="fas fa-file-excel"> Excel</i>',
          exportOptions: {
            columns: [0, 1, 2, 3, 4, 5, 6, 7, 8],
          },
        },
        {
          extend: "pdf",
          className: "btn btn-outline-dark export-btn",
          text: '<i class="fas fa-file-pdf"> PDF</i>',
          orientation: "landscape",
          pageSize: "LEGAL",
          exportOptions: {
            columns: [0, 1, 2, 3, 4, 5, 6, 7, 8],
          },
        },
      ];
    }
  }

  getTaxOffices() {
    this.apiUrl = environment.AUTHAPIURL + "tax-offices";

    this.httpClient.get<any>(this.apiUrl).subscribe((data) => {
      console.log("taxTaxOffices: ", data);
      this.taxTaxOffices = data.response;
    });
  }

  taxOfficeChanged(taxOfficeId) {
    this.taxOffice = this.taxTaxOffices.filter(
      (x) => x.id == taxOfficeId
    )[0]?.name;
  }

  getPaymentHistory(perpage, pageno) {
    this.spinnerService.show();
    //this.apiUrl = environment.AUTHAPIURL + "transactions-list/individual";
    this.apiUrl = `${environment.AUTHAPIURL}transactions-list/individual?page=` + pageno;
    const reqHeader = new HttpHeaders({
      "Content-Type": "application/json",
      Authorization: "Bearer " + localStorage.getItem("admin_access_token"),
    });

    //const obj = {
      // corporate_id: this.selectedCorporateId,
    //};
    this.searchObject["page_no"] = pageno;
    this.searchObject["per_page"] = perpage;

    this.httpClient
      .post<any>(this.apiUrl, this.searchObject, { headers: reqHeader })
      .subscribe((data) => {
        console.log("paymentHistoryData: ", data);
        this.paymentHistoryData =
          data.response == null ? [] : data.response.data;
        this.config.totalItems = data.response.total;
        this.apiResponseMessage = data.message;

        this.spinnerService.hide();
      });
  }

  pageChange(newPage: number) {
    this.paymentHistoryData = "";
    this.router.navigate(["/da-paymenthistory"], { queryParams: { page: newPage } });
    this.getPaymentHistory(this.config.itemsPerPage, newPage);
  }

  public setItemsPerPage(event) {
    this.config.itemsPerPage = event;
    this.currentPageLength = this.config.itemsPerPage;
    this.paymentHistoryData = "";
    this.router.navigate(["/da-paymenthistory"]);
    this.getPaymentHistory(this.config.itemsPerPage, 1);
  }

  onSubmitSearch(formAllData: any) {
    this.submitted = true;

    // stop the process here if form is invalid
    if (this.searchForm.invalid) {
      return;
    }

    this.searchObject = {
      tax_year: formAllData.taxYear,
      channel: formAllData.paymentChannel,
      taxpayer_name: formAllData.name,
      // surname: formAllData.name,
      date_start:
        this.daterange.start !== undefined ? this.daterange.start : null,
      date_end: this.daterange.end !== undefined ? this.daterange.end : null,
      tax_office_id: formAllData.taxOfficeID,
    };

    console.log("searchFormData: ", this.searchObject);
    this.getPaymentHistory(this.config.itemsPerPage, this.config.currentPage);
  }

  clearSearch() {
    this.paymentHistoryData = "";
    this.searchObject = {};
    this.initialiseForm();
    this.daterange = {};

    this.getPaymentHistory(this.config.itemsPerPage, this.config.currentPage);
  }
}
